                <h2>{this.state.count}</h2>

                <button className='btn btn-primary' onClick={()=>this.increase(1)}>+</button>
                <button className='btn btn-warning m-2' onClick={()=>this.decrease(1)}>-</button>


            </div>
            </>
        );
    }
}
 
