import React  , {Component}from 'react';


class Product extends Component {
    state = { 
        name : "Burger",
        count: 0,
        // imgUrl : "logo512.png",
        names : []//["moamed" , "ahmed" ,"ali"]
     } 
     
    render() {
        // const color = this.state.count == 0 ? "bg-warning text-light mx-3 p-3"  : "bg-primary"
        return (
            
            <>
            <div>
              {/* //  <img src={this.state.imgUrl} alt="" /> */}
                <span>{this.state.name}</span>
                <span className={this.setColor()}>{this.state.count}</span>
                <ul>
                    {this.state.names.map((name , index) =><li key={index}>{name} {index}</li>)}
                </ul>
            </div>
            </>
        );
    }
}
 
export default Product;
