import ShooppingCart from './Components/ShoppingCart';
import Navbar from './Components/Navbar';
import React ,{ Component } from 'react';


class App extends Component {
  state = {
    products: [
      { id: 1, name: "Burger", count: 2 },
      { id: 2, name: "Cola", count: 3 },
      { id: 3, name: "Shaowrma", count: 9 },
      { id: 4, name: "Salad", count: 5 },
    ],
  };

  handelDelete = (prod) => {
    //clone
    //edite
    const products = this.state.products.filter((p) => p.id !== prod.id);
    //  console.log(prod);
    //logic
    this.setState({ products });
  };

  handleReset = () => {
    //clone
    let products = [...this.state.products];
    products = products.map((p) => {
      p.count = 0;
      return p;
    });

    this.setState({ products });
  };

  handleIncrease = (product) => {
    //clone
    const products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...products[index] };
    //edit

    products[index].count++;

    //set state
    this.setState({ products });
  };

  render() {
    return (
      <div className="App container">
        <Navbar />
        <ShooppingCart onDelete={this.handelDelete}  on/>
      </div>
    );
  }
}

export default App;
