import React from 'react';
import { useParams } from 'react-router-dom';


export default function Details(props) {
    const params = useParams()
    const product = props.products.filter(
      (c) => c.id === parseInt(params.id)
    )[0];
    console.log(params);
  return (
    <>
      <h1>
        Details{" "}
        <span className="bg-success text-light btn btn-lg mx-3">
          {params.id}
        </span>
        <span className="bg-warning text-light btn btn-lg mx-3">
          {product.name}
        </span>
        <span className="bg-danger text-light btn btn-lg mx-3">
          {product || "optional and didnot enter"}
        </span>
      </h1>
    </>
  );
}
