import React, { Component } from "react";

class Product extends Component {
  state = {
    name: "Burger",
    count: 0,
    // imgUrl : "logo512.png",
    names: ["moamed", "ahmed", "ali"],
  };

  increase = (num) => {
    this.setState({ count: this.state.count + num });
  };

  decrease = (num) => {
    if (this.state.count === 0) {
      this.setState({ count: this.state.count });
    } else {
      this.setState({ count: this.state.count - num });
    }
  };

  render() {
    return (
      <>
        <div>

          {this.state.names.length === 0 && <h3>no names</h3>}
          {/* //  <img src={this.state.imgUrl} alt="" /> */}
          {/* <span>{this.state.name}</span>
                <span className={this.setColor()}>{this.state.count}</span> */}
          <ul>
            {this.state.names.map((name, index) => (
              <li
                key={index}
                className="border border-dark  text-dark my-2 py-2"
              >
                {name} {index}
              </li>
            ))}
          </ul>
          <h2>{this.state.count}</h2>

          <button className="btn btn-primary" onClick={() => this.increase(1)}>
            +
          </button>
          <button
            className="btn btn-warning m-2"
            onClick={() => this.decrease(1)}
          >
            -
          </button>
        </div>
      </>
    );
  }
}

export default Product;
