import React, { Component } from 'react';
import Product from './Product';



class ShooppingCart extends Component {
    state = { 
        products    :[
        {id:1 , name:'Burger' , count :2},
        {id:2 , name:'Cola' , count :3},
        {id:3 , name:'Shaowrma' , count :9},
        {id:4 , name:'Salad' , count :5}
    ]
     } 
     
   handelDelete = (prod)=>{
    //clone
    //edite
    const products = this.state.products.filter(p=> p.id !== prod.id)
    //  console.log(prod);
    //logic
    this.setState({products})
   }

   handleReset = ()=>{
    //clone
    let products = [...this.state.products]
    products  = products.map(p=>{
            p.count = 0;
            return p;            
    })


    this.setState({products})
   }
    render() { 
        
        return (
            <>
            <h1 className='bg-success text-light p-4'>ShooppingCart</h1>
            <button className='btn btn-secondary mx-5' onClick={this.handleReset}>Reset</button>
             {this.state.products.map(myprod=>{
             
             return <Product key={myprod.id} pr={myprod} onDelete = {this.handelDelete}/>
                })}
            </>

        );
    }
}
 
export default ShooppingCart;
