          <Route path="/contact" Component={Conatct} />
          <Route path='/cart' element={<ShooppingCart products={this.state.products} onIncrement={this.handleIncrease} onDel={this.handelDelete} onReset={this.handleReset} />}/>
          <Route path="/" Component={Home} />
        </Routes>
        export default withRouter(Home)
