import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";

class Product extends Component {
  // to solve single source of truth
  // state = {
  //   name: this.props.pr.name,
  //   count: this.props.pr.count
  // };
  ///////////////////////////////////

  increase = (num) => {
    this.setState({ count: this.props.pr.count + num });
  };

  decrease = (num) => {
    if (this.props.pr.count === 0) {
      this.setState({ count: this.state.count });
    } else {
      this.setState({ count: this.state.count - num });
    }
  };

  render() {
    return (
      <>
        <div className="row">
          <div className="col-2">
            <span>{thi.name}</span>
          </div>
          <div className="col flex">
            <span>{thi.count}</span>

            <button
              className="btn btn-primary btn-sm mx-3"
              onClick={() => this.increase(1)}
            >
              +
            </button>
            <button className="btn" onClick={()=>this.props.onDelete(this.props.pr)}>
              <FontAwesomeIcon icon={faTrash} />
            </button>
          </div>
        </div>
      </>
    );
  }
}

export default Product;
