import React, { Component } from 'react';
import Product from './Product';



class ShooppingCart extends Component {
 
  render() {
    return (
      <>
        <h1 className="bg-success text-light p-4">ShooppingCart</h1>
        <button className="btn btn-secondary mx-5" onClick={this.handleReset}>
          Reset
        </button>
        {this.state.products.map((myprod) => {
          return (
            <Product key={myprod.id} pr={myprod} onDelete={this.handelDelete} onIncreas={this.handleIncrease}/>
          );
        })}
      </>
    );
  }
}
 
export default ShooppingCart;
