  increase = (num) => {
    this.setState({ count: this.props.pr.count + num });
  };

  decrease = (num) => {
    if (this.props.pr.count === 0) {
      this.setState({ count: this.state.count });
    } else {
      this.setState({ count: this.state.count - num });
    }
  };

